mapper.select:
    SELECT w.*,b.*
	FROM works w
	JOIN business b on w.work_bus=b.bus_id


				and work_id=#{workId}


				and work_bus=#{workBus.busId}


				and work_major <= #{workMajor}


				and work_exp <= #{workExp}


				and work_det = #{workDet}


				and work_sal = #{workSal}


				and work_sta = #{workSta}
